<?phpload("Request, Friends, Player");data("friend");//check if friendsif(I("Friends")->get(USER, $friend) || I("Friends")->get($friend, USER)) {	error("Already friends");}//if already waiting on a request$r = I("Request")->get($friend, USER);if($r) {	//make them friends if they both requested eachother	I("Friends")->create(USER, $friend);	$r->remove();		ok();}I("Request")->create(USER, $friend, NOW());ok();?>